  } catch (err) {
    // eslint-disable-next-line no-console
    console.error('Failed to start server:', err);
    process.exit(1);
  }
})();

// Compute basic score statistics from a list of attempts
module.exports = function calculateScore(attempts = []) {
  const total = attempts.length;
  const correct = attempts.filter((a) => !!a.isCorrect).length;
  const incorrect = total - correct;
  const accuracy = total ? correct / total : 0;

  // Calculate current and longest correct streaks based on attemptedAt order (assumed DESC in consumers)
  let currentStreak = 0;
  let longestStreak = 0;
  // Sort ascending by attemptedAt for streak calculation
  const ordered = [...attempts].sort((a, b) => new Date(a.attemptedAt) - new Date(b.attemptedAt));
  for (const at of ordered) {
    if (at.isCorrect) {
      currentStreak += 1;
      longestStreak = Math.max(longestStreak, currentStreak);
    } else {
      currentStreak = 0;
    }
  }

  return { total, correct, incorrect, accuracy, longestStreak };
};

// Build a detailed analytics report from an array of attempts
module.exports = function generateReport(attempts = []) {
  const byQuestion = new Map();

  for (const a of attempts) {
    const qId = a.questionId;
    const entry = byQuestion.get(qId) || {
      questionId: qId,
      questionText: a.question?.text || '',
      attempts: 0,
      correct: 0,
      lastAttemptedAt: null,
      lastAnswer: a.answer ? { id: a.answer.id, text: a.answer.text, isCorrect: a.answer.isCorrect } : null,
      mistakes: {},
    };
    entry.attempts += 1;
    if (a.isCorrect) entry.correct += 1;
    if (!entry.lastAttemptedAt || new Date(a.attemptedAt) > new Date(entry.lastAttemptedAt)) {
      entry.lastAttemptedAt = a.attemptedAt;
      entry.lastAnswer = a.answer ? { id: a.answer.id, text: a.answer.text, isCorrect: a.answer.isCorrect } : entry.lastAnswer;
    }
    if (a.answer && !a.isCorrect) {
      entry.mistakes[a.answer.text] = (entry.mistakes[a.answer.text] || 0) + 1;
    }
    byQuestion.set(qId, entry);
  }

  const questions = Array.from(byQuestion.values()).map((q) => ({
    ...q,
    accuracy: q.attempts ? q.correct / q.attempts : 0,
    topMistakes: Object.entries(q.mistakes)
      .sort((a, b) => b[1] - a[1])
      .slice(0, 3)
      .map(([text, count]) => ({ text, count })),
  }));

  // Overall stats
  const total = attempts.length;
  const correct = attempts.filter((a) => a.isCorrect).length;
  const incorrect = total - correct;
  const accuracy = total ? correct / total : 0;

  return {
    total,
    correct,
    incorrect,
    accuracy,
    questions,
  };
};

// Shuffle an array in place using Fisher-Yates algorithm
function shuffleAnswers(answers) {
  for (let i = answers.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [answers[i], answers[j]] = [answers[j], answers[i]];
  }
  return answers;
}

module.exports = shuffleAnswers;

import React from 'react';

export default function Button({ children, variant = 'primary', disabled, onClick, type = 'button', style, ...rest }) {
  const base = {
    padding: '8px 12px',
    borderRadius: 6,
    border: '1px solid transparent',
    cursor: disabled ? 'not-allowed' : 'pointer',
    opacity: disabled ? 0.6 : 1,
    background: variant === 'primary' ? '#2563eb' : '#e5e7eb',
    color: variant === 'primary' ? '#fff' : '#111827',
    ...style,
  };
  return (
    <button type={type} disabled={disabled} onClick={onClick} style={base} {...rest}>
      {children}
    </button>
  );
}

